electraAcRecordedSender::electraAcRecordedSender(int _IRledPin) : IRledPin(_IRledPin)
{
    pinMode(IRledPin, OUTPUT);
}

void electraAcRecordedSender::sendAcCoolCommand(int temp)
{
    SendBuffer(coolIrBuffer[temp]);
}

void electraAcRecordedSender::sendAcHeatCommand(int temp)
{
    SendBuffer(heatIrBuffer[temp]);
}

// This procedure sends a 38KHz pulse to the IRledPin
// for a certain # of microseconds. We'll use this whenever we need to send codes
void electraAcRecordedSender::pulseIR(long microsecs)
{
    // we'll count down from the number of microseconds we are told to wait

    cli(); // this turns off any background interrupts

    while (microsecs > 0)
    {
        // 38 kHz is about 13 microseconds high and 13 microseconds low
        digitalWrite(IRledPin, HIGH); // this takes about 3 microseconds to happen
        delayMicroseconds(10);        // hang out for 10 microseconds, you can also change this to 9 if its not working
        digitalWrite(IRledPin, LOW);  // this also takes about 3 microseconds
        delayMicroseconds(10);        // hang out for 10 microseconds, you can also change this to 9 if its not working

        // so 26 microseconds altogether
        microsecs -= 26;
    }

    sei(); // this turns them back on
}

void electraAcRecordedSender::SendBuffer(std::vector<int> buf)
{
    int length = buf.size();
    for (unsigned int i = 0; i < length; ++i)
    {
        if (i & 1)
            delayMicroseconds(buf[i]);
        else
            pulseIR(buf[i]);
    }
}
